def.body:
case self
in Firefox
  {"browserName" => "firefox", "acceptInsecureCerts" => true}
in Chrome, Chromium
  {"browserName" => "chrome", "version" => "", "platform" => "ANY"}
in Edge
  {"browserName" => "MicrosoftEdge", "version" => "", "platform" => "ANY"}
in InternetExplorer
  {"browserName" => "internet explorer", "version" => "", "platform" => "WINDOWS"}
in Opera
  {"browserName" => "opera", "version" => "", "platform" => "ANY"}
in Safari
  {"browserName" => "safari", "version" => "", "platform" => "MAC"}
in PhantomJS
  {"browserName" => "phantomjs", "version" => "", "platform" => "ANY", "javascriptEnabled" => true}
in Android
  {"browserName" => "android", "version" => "", "platform" => "ANDROID"}
in WebkitGTK
  {"browserName" => "MiniBrowser", "version" => "", "platform" => "ANY"}
in WPEWebkit
  {"browserName" => "MiniBrowser", "version" => "", "platform" => "ANY"}
end

if int = json.as_i?
  if CODE_MAP.has_key?(int)
    return CODE_MAP[int].new
  end
else
  if str = json.as_s?
    {% if true %}
          case str
          {% for code, error in CODE_MAP %}
          when /{{ (error.id.underscore.gsub(/_/, " ")).id }}/
            return {{ error.id }}.new(str)
          {% end %}
          else
            return GenericError.new(str)
          end
        {% end %}
  end
end
GenericError.new(json.to_s)

if @session.w3c?
  @session.execute("SetWindowRect", {"height" => rect.height, "width" => rect.width, "x" => rect.x, "y" => rect.y})
else
  @session.stop
  raise(Error::UnknownMethod.new("`Window#rect=` is only supported on W3C compatible drivers."))
end
